parameters.jsCode:
// Get the AI report data
const report = $input.first().json.output;

// Simple, clean professional HTML with new schema structure
const newsletterHTML = `
<!DOCTYPE html>
<html>
<head>
    <meta charset="UTF-8">
    <meta name="viewport" content="width=device-width, initial-scale=1.0">
    <style>
        body {
            font-family: Arial, sans-serif;
            line-height: 1.6;
            color: #333;
            max-width: 600px;
            margin: 0 auto;
            padding: 20px;
            background: #ffffff;
        }
        h1 {
            color: #2c3e50;
            border-bottom: 3px solid #3498db;
            padding-bottom: 10px;
        }
        h2 {
            color: #2980b9;
            margin-top: 30px;
            margin-bottom: 15px;
        }
        .summary {
            background: #f8f9fa;
            padding: 15px;
            border-left: 4px solid #3498db;
            margin-bottom: 25px;
        }
        .article {
            border-bottom: 1px solid #eee;
            padding: 15px 0;
            margin-bottom: 15px;
        }
        .article h3 {
            color: #2c3e50;
            margin: 0 0 8px 0;
        }
        .source {
            color: #7f8c8d;
            font-size: 12px;
            margin-top: 8px;
        }
        .source a {
            color: #3498db;
            text-decoration: none;
            font-weight: bold;
        }
        .source a:hover {
            text-decoration: underline;
        }
        .read-more {
             display: inline-block;
             background: #3498db;
             color: white !important;
             padding: 6px 12px;
             border-radius: 4px;
             text-decoration: none;
             font-size: 12px;
             margin-top: 8px;
        }
        .read-more:hover {
            background: #2980b9;
            color: white;
            text-decoration: none;
        }
        .model-item {
            background: #e8f4fd;
            padding: 12px;
            margin: 8px 0;
            border-radius: 5px;
            border-left: 4px solid #3498db;
        }
        .tool-item {
            background: #f0f8e8;
            padding: 12px;
            margin: 8px 0;
            border-radius: 5px;
            border-left: 4px solid #27ae60;
        }
        .opensource-item {
            background: #fef9e7;
            padding: 12px;
            margin: 8px 0;
            border-radius: 5px;
            border-left: 4px solid #f39c12;
        }
        .early-access {
            background: #fdebf7;
            padding: 12px;
            margin: 8px 0;
            border-radius: 5px;
            border-left: 4px solid #e91e63;
        }
        .insight {
            background: #fff3cd;
            padding: 12px;
            margin: 8px 0;
            border-radius: 5px;
            border-left: 4px solid #ffc107;
        }
        .footer {
            margin-top: 40px;
            padding-top: 20px;
            border-top: 1px solid #eee;
            text-align: center;
            color: #7f8c8d;
            font-size: 12px;
        }
    </style>
</head>
<body>
    <h1>ü§ñ AI Creator Intelligence Report</h1>
    <p><strong>Date:</strong> ${new Date(report.metadata?.reportDate || Date.now()).toLocaleDateString('en-US', { 
        weekday: 'long', 
        year: 'numeric', 
        month: 'long', 
        day: 'numeric' 
    })}</p>

    <div class="summary">
        <h2>üìä Executive Summary</h2>
        <p>${report.executiveSummary || 'AI developments for content creators analyzed.'}</p>
    </div>

    ${report.newModels && report.newModels.length > 0 ? `
    <h2>üöÄ New AI Models & Launches</h2>
    ${report.newModels.map(item => `
        <div class="model-item">
            <h3>${item.modelName} by ${item.company}</h3>
            <p>${item.capabilities}</p>
            <p><strong>For Creators:</strong> ${item.relevanceToCreators}</p>
            <p><strong>Access:</strong> ${item.accessInfo}</p>
            <div class="source">
                Source: <a href="${item.url || '#'}" target="_blank">${item.source}</a>
                ${item.url ? `<br><a href="${item.url}" target="_blank" class="read-more">üìñ Read Full Article</a>` : ''}
            </div>
        </div>
    `).join('')}
    ` : ''}

    ${report.creatorTools && report.creatorTools.length > 0 ? `
    <h2>üõ†Ô∏è Creator Tools & Applications</h2>
    ${report.creatorTools.map(item => `
        <div class="tool-item">
            <h3>${item.toolName}</h3>
            <p>${item.description}</p>
            <p><strong>Use Case:</strong> ${item.useCase}</p>
            <p><strong>Pricing:</strong> ${item.pricing}</p>
            <p><strong>Availability:</strong> ${item.availability}</p>
            <div class="source">
                Source: <a href="${item.url || '#'}" target="_blank">${item.source}</a>
                ${item.url ? `<br><a href="${item.url}" target="_blank" class="read-more">üìñ Read Full Article</a>` : ''}
            </div>
        </div>
    `).join('')}
    ` : ''}

    ${report.openSource && report.openSource.length > 0 ? `
    <h2>üîì Open Source Releases</h2>
    ${report.openSource.map(item => `
        <div class="opensource-item">
            <h3>${item.name}</h3>
            <p>${item.description}</p>
            <p><strong>Platform:</strong> ${item.platform}</p>
            <p><strong>Creator Applications:</strong> ${item.creatorApplications}</p>
            <div class="source">
                Source: <a href="${item.url || '#'}" target="_blank">${item.source}</a>
                ${item.url ? `<br><a href="${item.url}" target="_blank" class="read-more">üìñ Read Full Article</a>` : ''}
            </div>
        </div>
    `).join('')}
    ` : ''}

    ${report.agentNews && report.agentNews.length > 0 ? `
    <h2>ü§ñ AI Agents & Automation</h2>
    ${report.agentNews.map(item => `
        <div class="article">
            <h3>${item.agentName}</h3>
            <p>${item.description}</p>
            <p><strong>Capabilities:</strong> ${item.capabilities}</p>
            <p><strong>Creator Use Case:</strong> ${item.creatorUseCase}</p>
            <div class="source">
                Source: <a href="${item.url || '#'}" target="_blank">${item.source}</a>
                ${item.url ? `<br><a href="${item.url}" target="_blank" class="read-more">üìñ Read Full Article</a>` : ''}
            </div>
        </div>
    `).join('')}
    ` : ''}

    ${report.bigTechMoves && report.bigTechMoves.length > 0 ? `
    <h2>üè¢ Big Tech AI Moves</h2>
    ${report.bigTechMoves.map(item => `
        <div class="article">
            <h3>${item.company}: ${item.announcement}</h3>
            <p><strong>Impact on Creators:</strong> ${item.impactOnCreators}</p>
            <p><strong>Timeline:</strong> ${item.timeline}</p>
            <div class="source">
                Source: <a href="${item.url || '#'}" target="_blank">${item.source}</a>
                ${item.url ? `<br><a href="${item.url}" target="_blank" class="read-more">üìñ Read Full Article</a>` : ''}
            </div>
        </div>
    `).join('')}
    ` : ''}

    ${report.earlyAccess && report.earlyAccess.length > 0 ? `
    <h2>‚≠ê Early Access Opportunities</h2>
    ${report.earlyAccess.map(item => `
        <div class="early-access">
            <h3>${item.productName} by ${item.company}</h3>
            <p>${item.description}</p>
            <p><strong>How to Access:</strong> ${item.howToAccess}</p>
            <p><strong>Deadline:</strong> ${item.deadline}</p>
            <div class="source">
                Source: <a href="${item.url || '#'}" target="_blank">${item.source}</a>
                ${item.url ? `<br><a href="${item.url}" target="_blank" class="read-more">üìñ Read Full Article</a>` : ''}
            </div>
        </div>
    `).join('')}
    ` : ''}

    ${report.creatorInsights && report.creatorInsights.length > 0 ? `
    <h2>üí° Creator Insights</h2>
    ${report.creatorInsights.map((item, index) => `
        <div class="insight">
            <strong>${index + 1}. ${item.insight}</strong><br>
            ${item.opportunity}<br>
            <strong>Action:</strong> ${item.actionForCreators}
        </div>
    `).join('')}
    ` : ''}

    <div class="footer">
        <p><strong>AI Creator Intelligence System</strong></p>
        <p>Powered by n8n Automation | Generated on ${report.metadata?.reportDate || new Date().toISOString().split('T')[0]}</p>
        <p>Analyzed ${report.metadata?.totalArticles || 'multiple'} articles from trusted sources</p>
        <p style="margin-top: 15px;">
            üí° <em>Click "Read Full Article" links to access complete source articles</em>
        </p>
    </div>
</body>
</html>
`;

return [{
  json: {
    emailHTML: newsletterHTML,
    subject: `ü§ñ AI Daily News Report - ${new Date().toLocaleDateString('en-US', { month: 'short', day: 'numeric' })}`,
    reportDate: report.metadata?.reportDate || new Date().toISOString().split('T')[0],
    reportTitle: report.reportTitle || 'AI Creator Intelligence Report'
  }
}];

parameters.inputSchema:
{
  "type": "object",
  "properties": {
    "filteredArticles": {
      "type": "array",
      "description": "Top articles ranked for content creator relevance",
      "items": {
        "type": "object",
        "properties": {
          "title": {
            "type": "string",
            "description": "Article headline"
          },
          "summary": {
            "type": "string", 
            "description": "Clean 2-3 sentence summary"
          },
          "source": {
            "type": "string",
            "description": "Publication name"
          },
          "url": {
            "type": "string",
            "description": "Direct link to article"
          },
          "score": {
            "type": "number",
            "description": "Relevance score 1-100"
          },
          "category": {
            "type": "string",
            "enum": ["model_launch", "creator_tool", "open_source", "agent_news", "big_tech", "early_access", "funding", "research"],
            "description": "Content creator category"
          },
          "creatorRelevance": {
            "type": "string", 
            "description": "Why this matters for content creators"
          },
          "accessInfo": {
            "type": "string",
            "description": "How creators can access/use this (free, paid, waitlist, etc.)"
          },
          "urgency": {
            "type": "string",
            "enum": ["breaking", "trending", "standard"],
            "description": "News urgency level"
          },
          "publishDate": {
            "type": "string",
            "description": "When article was published"
          }
        },
        "required": ["title", "summary", "source", "score", "category", "creatorRelevance"]
      }
    },
    "processingStats": {
      "type": "object",
      "properties": {
        "totalProcessed": {
          "type": "number",
          "description": "Total articles analyzed"
        },
        "filtered": {
          "type": "number", 
          "description": "Articles returned after filtering"
        },
        "topCategories": {
          "type": "array",
          "items": {"type": "string"},
          "description": "Most common categories found"
        },
        "avgScore": {
          "type": "number",
          "description": "Average relevance score"
        }
      }
    }
  },
  "required": ["filteredArticles", "processingStats"]
}
