// Calculate how many search pages we need
const profilesNeeded = parseInt($('HR Job Search Form').first().json['Number of Profiles to Find']);
const resultsPerPage = 10; // Google returns 10 results per page
const pagesNeeded = Math.ceil(profilesNeeded / resultsPerPage);

// Create an array of page objects for Split In Batches to iterate over
const pages = [];
for (let i = 0; i < pagesNeeded; i++) {
    pages.push({
        json: {
            pageNumber: i + 1,
            startIndex: i * resultsPerPage,
            totalPages: pagesNeeded,
            profilesNeeded: profilesNeeded
        }
    });
}

console.log(`Need to fetch ${pagesNeeded} pages to get ${profilesNeeded} profiles`);
return pages;

// Extract LinkedIn URLs from Google Custom Search API response
const response = $input.first().json;
const pageInfo = $('Split In Batches').item.json;

if (!response.items || !Array.isArray(response.items)) {
    console.log(`Page ${pageInfo.pageNumber}: No search results found`);
    return [];
}

const linkedInProfiles = response.items
    .filter(item => {
        return item.link && 
               item.link.includes('linkedin.com/in/') &&
               !item.link.includes('google.com');
    })
    .map(item => {
        let url = item.link.split(/[?#]/)[0];
        url = url.replace(/\/$/, '');

        return {
            json: {
                linkedin_url: url,
                page_number: pageInfo.pageNumber
            }
        };
    });

console.log(`Page ${pageInfo.pageNumber}: Found ${linkedInProfiles.length} LinkedIn profiles`);
return linkedInProfiles;

// Collect all unique LinkedIn URLs from all pages
const allItems = $input.all();
const uniqueUrls = new Map();

// Deduplicate URLs
allItems.forEach(item => {
    if (item.json.linkedin_url) {
        uniqueUrls.set(item.json.linkedin_url, item.json.linkedin_url);
    }
});

const urlArray = Array.from(uniqueUrls.values());
console.log(`Collected ${urlArray.length} unique LinkedIn URLs total`);

// Prepare for APIFY
return [{
    json: {
        profileUrls: urlArray,
        totalUrls: urlArray.length
    }
}];

// Process APIFY LinkedIn data
const items = [];

// Get all items from the previous node - they come as individual JSON objects
const profiles = $items().map(item => item.json);

console.log(`Processing ${profiles.length} profiles from APIFY`);

profiles.forEach(profile => {
    if (!profile) return;

    // Extract skills from the complex structure
    let skillsList = '';
    if (profile.skills && Array.isArray(profile.skills)) {
        skillsList = profile.skills
            .map(skill => skill.title || '')
            .filter(title => title.length > 0)
            .slice(0, 10) // Limit to first 10 skills
            .join(', ');
    }

    // Extract education information
    let educationList = '';
    if (profile.educations && Array.isArray(profile.educations)) {
        educationList = profile.educations
            .map(edu => {
                const parts = [];
                if (edu.title) parts.push(edu.title);
                if (edu.subtitle) parts.push(edu.subtitle);
                if (edu.caption) parts.push(`(${edu.caption})`);
                return parts.join(' - ');
            })
            .filter(edu => edu.length > 0)
            .join('; ');
    }

    // Extract certifications
    let certifications = '';
    if (profile.licenseAndCertificates && Array.isArray(profile.licenseAndCertificates)) {
        certifications = profile.licenseAndCertificates
            .map(cert => cert.title || '')
            .filter(title => title.length > 0)
            .join(', ');
    }

    const processedProfile = {
        linkedin_url: profile.linkedinUrl || '',
        full_name: profile.fullName || profile.firstName + ' ' + profile.lastName || '',
        headline: profile.headline || '',
        email: profile.email || 'Not provided',
        current_job_title: profile.jobTitle || '',
        current_company: profile.companyName || 'Not specified',
        company_industry: profile.companyIndustry || 'Not specified',
        experience_years: profile.currentJobDurationInYrs || 0,
        location: profile.addressWithCountry || profile.addressCountryOnly || '',
        skills: skillsList || 'Not listed',
        education: educationList || 'Not provided',
        about_summary: (profile.about || '').substring(0, 500) || 'No summary available',
        connections: profile.connections || 0,
        certifications: certifications || 'None listed'
    };

    items.push({ json: processedProfile });
});

console.log(`Successfully processed ${items.length} LinkedIn profiles`);
return items;

